import React, { useState, useEffect } from 'react';
import axios from 'axios';
import Slider from "react-slick";
import classes from './MovieList.module.css'; // Import CSS module

function MovieList({ title, fetchUrl, isLargeRow = false, isOriginal = false }) {
    const [movies, setMovies] = useState([]);

    useEffect(() => {
        const fetchData = async () => {
            try {
                const request = await axios.get(fetchUrl);
                setMovies(request.data.results);
            } catch (error) {
                console.error("Lỗi khi lấy dữ liệu từ API", error);
            }
        };

        fetchData();
    }, [fetchUrl]);

    const base_url = "https://image.tmdb.org/t/p/original/";

    const sliderSettings = {
        dots: false,
        infinite: true,
        speed: 500,
        slidesToShow: 6,
        slidesToScroll: 6,
        responsive: [
            {
                breakpoint: 1024,
                settings: {
                    slidesToShow: 5,
                    slidesToScroll: 5,
                    infinite: true,
                },
            },
            {
                breakpoint: 600,
                settings: {
                    slidesToShow: 3,
                    slidesToScroll: 3,
                    initialSlide: 2,
                },
            },
            {
                breakpoint: 480,
                settings: {
                    slidesToShow: 2,
                    slidesToScroll: 2,
                },
            },
        ],
    };

    return (
        <div className={classes.movieList}>
            <h2>{title}</h2>
            <div className={classes.movieList__posters}>
                {isOriginal ? (
                    <div className={classes.originalsGrid}>
                        {movies.slice(0, 10).map((movie) => (
                            <img
                                key={movie.id}
                                className={`${classes.movieList__poster} ${classes.movieList__posterLarge}`}
                                src={`${base_url}${movie.poster_path}`}
                                alt={movie.name || movie.title}
                            />
                        ))}
                    </div>
                ) : (
                    <Slider {...sliderSettings}>
                        {movies.map((movie) => (
                            <img
                                key={movie.id}
                                className={classes.movieList__poster}
                                src={`${base_url}${movie.backdrop_path}`}
                                alt={movie.name || movie.title}
                                onMouseEnter={() => {} /* Add hover animation here */}
                            />
                        ))}
                    </Slider>
                )}
            </div>
        </div>
    );
}

export default MovieList;
